import {useState} from 'react';
import { useTranslation } from 'react-i18next';

import './nav.sass'
const Nav = () => {
    const { t, i18n } = useTranslation();
    const activeLang = localStorage.getItem("lang") ? localStorage.getItem("lang"): "En";
    const [lang, setLang] = useState(activeLang)
    const changeLanguage = (language) => {
        i18n.changeLanguage(language);
    };
    const changeText = (lang) => {
        setLang(lang)
        localStorage.setItem("lang", lang)
    }
    return(
        <nav>
            <div className="wrap">
                <div className="nav">
                    <div className="nav__left">
                        <div className="logo">
                            <img src="./img/header/logo.svg" alt="INCOM" />
                        </div>
                        <div className="menu">
                            <a href="#" className="menu__item">{t("home")}</a>
                            <a href="#" className="menu__item">Недвижимость</a>
                            <a href="#" className="menu__item">Услуги</a>
                            <a href="#" className="menu__item">О нас</a>
                            <a href="#" className="menu__item">Контакты</a>
                        </div>
                    </div>
                    <div className="nav__right">
                        <div className="lang">
                            <div className="lang__active">{lang}</div>
                            {/* <div className="long__ru">Ru</div> */}
                            <div className="lang__wrap">
                                <div className="lang__arrow">
                                    <img src="./img/arrow.svg" alt=""/>
                                </div>
                                <div className="lang-menu__wrap">
                                    <div className="lang-menu">
                                        <div className="lang__item" onClick={() => {
                                            changeText('En')
                                            changeLanguage('en')
                                        }}>EN</div>
                                        <div className="lang__item" onClick={() => {
                                            changeText('Ru')
                                            changeLanguage('ru')
                                        }}>RU</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </nav>
    )
}

export default Nav;
