import { useTranslation } from "react-i18next";
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Pagination } from "swiper";
import 'swiper/css';
import 'swiper/modules/pagination/pagination'
import "./cards.sass";
import { useState } from "react";

SwiperCore.use([Pagination]);

const Cards = () => {
    const { t, i18n } = useTranslation();
    const [swiper, setSwiper] = useState(null);
    return(
        <div className="wrap">
            <div className="cards">
                <div className="title">{t("top")}</div>
                <div className="cards__wrap">
                    <Swiper
                        spaceBetween={24}
                        slidesPerView={1}
                        centeredSlides={true}
                        modules={[Pagination]}
                        pagination={{ clickable: true }}
                        // breakpoints={{
                        //     // when window width is >= 640px
                        //     0: {
                        //         spaceBetween: 10,
                        //         slidesPerView: 1.2,
                        //     },
                        //     500: {
                        //         spaceBetween: 24,
                        //         slidesPerView: 1.5,
                        //     },
                        //     // when window width is >= 768px
                        //     800: {
                        //         slidesPerView: 3,
                        //     }
                        //   }}
                    >   
                        <SwiperSlide>
                            <div className="card">
                                <div className="card__image card__image_one">
                                </div>
                            </div>
                        </SwiperSlide> 
                        <SwiperSlide>
                            <div className="card">
                                <div className="card__image card__image_two">
                                </div>
                            </div>
                        </SwiperSlide> 
                        <SwiperSlide>
                            <div className="card">
                                <div className="card__image card__image_three">
                                </div>
                            </div>
                        </SwiperSlide> 
                        <SwiperSlide>
                            <div className="card">
                                <div className="card__image card__image_four">
                                </div>
                            </div>
                        </SwiperSlide> 
                        <SwiperSlide>
                            <div className="card">
                                <div className="card__image card__image_five">
                                </div>
                            </div>
                        </SwiperSlide> 
                    </Swiper>
                </div>
            </div>
        </div>
    )
}
export default Cards;
